package contest.yandexpreparation

fun main() {
    println(checkInclusion("ab", "ibcaooo")) // false
    println(checkInclusion("ab", "ibaooo")) // true
    println(checkInclusion("adc", "dcda")) // true
    println(checkInclusion("hello", "ooolleoooleh")) // false
    println(
        checkInclusion(
            "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz",
            ""
        )
    ) // true
    println(checkInclusion("adc", "daddc")) // false
    println(
        checkInclusion(
            "",
            ""
        )
    ) // true
}

private fun checkInclusion(s1: String, s2: String): Boolean {
    val lettersCount = s1.fold(mutableMapOf<Char, Int>()) { map, c -> map.also { it[c] = (it[c] ?: 0) + 1 } }
    var zeroedCount = 0
    var left = 0

    for (i in s2.indices) {
        val charCount = lettersCount[s2[i]]

        if (charCount == null) {
            while (left < i) {
                lettersCount[s2[left]] = lettersCount[s2[left]]!! + 1
                ++left
            }
            zeroedCount = 0
            left = i + 1
            continue
        } else if (charCount == 0) {
            while (left < i && s2[left] != s2[i]) {
                if (lettersCount[s2[left]] == 0) --zeroedCount
                lettersCount[s2[left]] = lettersCount[s2[left]]!! + 1
                ++left
            }
            if (s2[left] == s2[i]) ++left
        } else {
            if (charCount == 1) ++zeroedCount
            lettersCount[s2[i]] = charCount - 1
        }

        if (lettersCount.size == zeroedCount) return true
    }

    return lettersCount.size == zeroedCount
}
